package ru.weathercities.outputs;import lombok.RequiredArgsConstructor;import org.springframework.stereotype.Service;import org.springframework.web.client.RestTemplate;import ru.weathercities.dto.accuweather.AccuweatherDto;@Service@RequiredArgsConstructorpublic class AccuweatherService {    private final RestTemplate restTemplate;    private final String API_URL =            "http://dataservice.accuweather.com/forecasts/v1/hourly/1hour/{code}?apikey=iNmWANt2U7CwY7X2iWEXkvwvXFPC9nUq";    public Double getWeather(String code) {        String url = API_URL.replace("{code}", code);        AccuweatherDto a = restTemplate.getForEntity(url, AccuweatherDto.class).getBody();        return (a.getTemperature().getValue() - 32) / 1.8;    }}